def number_of_handle (length_array)
   length_array = array.length
   puts "Dans cette array , il y a : #{length_array} handles"
   return
end

def short_handle (plus_court)
   plus_court = array.min {|a,b| a.length <=> b.length}
   puts "Le handle le plus court est :#{plus_court}"
   return
end

def handle_with_five_character (handle_five)
    n = 5
    handle_five = array.select {|word| word.length == n}
    puts "Il y a : #{handle_five.count} handle"
    return
end

def capitalize_handle (capitalize)
    capitalize = array.split.map(&:capitalize).join(' ').count
    return
end

def alphabetic_order (order)
    down = array.map(&:downcase)
    order = down.sort
    puts order
    return
end

def min_maximum (min_max)
    min_max = array.sort{|a, b| a.length <=> b.length}
    puts min_max
    return
end


